msg.Body = msg.payload;
msg.Bucket = flow.get("s3").bucket;
msg.Key = flow.get("s3").prefix + msg.filename;

// node.warn(msg);

return msg;

var m = [];
for (var i in msg.payload.aircraft) {
    m.push({
        payload: msg.payload.aircraft[i],
        timestamp: msg.timestamp,
        source: msg.source,
    });
}
return [m];

/*
If msg.payload is an array containing two objects,
the first object will be written as the set of named fields,
the second is the set of named tags.
*/
p = msg.payload;

fields = {
    lat: p.lat,
    lon: p.lon,
    rssi: p.rssi,
    time: Math.round((msg.timestamp - p.seen_pos) * 1000), // ugh
};

if (p.alt_baro) {
    fields.alt_baro = p.alt_baro;
}

if (p.alt_geom) {
    fields.alt_geom = p.alt_geom;
}

if (p.gs) {
    fields.gs = p.gs;
}


tags = {
    hex: p.hex,
    source: msg.source,
};

if (p.squawk) {
    tags.squawk = p.squawk;
}

if (p.emergency) {
    tags.emergency = p.emergency;
}

if (p.flight) {
    tags.flight = p.flight;
}

if (p.category) {
    tags.category = p.category;
}

msg = {
    payload: [
        // fields
        fields,

        // tags
        tags,
]};

return msg;

async function processLineByLine() {
  const fileStream = fs.createReadStream('/tmp/filtered_flights.json');

  const rl = readline.createInterface({
    input: fileStream,
    crlfDelay: Infinity
  });
  // Note: we use the crlfDelay option to recognize all instances of CR LF
  // ('\r\n') in input.txt as a single line break.

  for await (const line of rl) {
    node.send({ payload: JSON.parse(line) });
  }
}

processLineByLine();
node.done();
